mapper.select:
select t.rec_id as recid, t.company_code as companyCode, t.AGENT_CODE as agentCode, t.fee as fee, t.fee_code as feeType, decode(t.fee_code, 'ALL', '适用全部', (select f.fee_dsc from ctas_fee_type f where f.fee_type = t.fee_code and rownum = 1)) as feeDesc, t.bill_no as billDate, t.effective_bill_no as effectiveBillNo, t.if_deduct_weight as isDeduct, t.create_time as createTime, t.creator_id as creatorId, t.creator_name as creatorName, t.last_update_time as lastUpdateTime, t.last_updator_id as lastUpdatorId, t.last_updator_name as lastUpdatorName, t.remark, t.di, t.ei from CTAS_AGT_ADJUST t WHERE 1 = 1
AND t.AGENT_CODE = #{agentCode,jdbcType=VARCHAR}
AND t.DI = #{di,jdbcType=VARCHAR}
AND t.EI = #{ei,jdbcType=VARCHAR}
AND t.bill_no = #{billDate,jdbcType=VARCHAR}
AND t.fee_code = #{feeType,jdbcType=VARCHAR}

select
sum(t.fee) as fee
from CTAS_AGT_ADJUST t
WHERE 1 = 1

    AND t.AGENT_CODE = #{agentCode,jdbcType=VARCHAR}


    AND t.DI = #{di,jdbcType=VARCHAR}


    AND t.EI = #{ei,jdbcType=VARCHAR}


    AND t.bill_no = #{billDate,jdbcType=VARCHAR}


    AND t.fee_code = #{feeType,jdbcType=VARCHAR}
