mapper.select:
select T.REC_ID, T.PARTITION_OWNER, T.PARTITION_ID, T.AGENT_CODE, T.CRT_OPE_TIME,
T.COMPANY_CODE, T.DI, T.GROUP_NAME_CN, T.GROUP_NAME_EN
from CTAS_AGT_GROUP T
WHERE T.COMPANY_CODE = #{companyCode,jdbcType=VARCHAR}

    AND T.GROUP_NAME_CN = #{groupNameCn,jdbcType=VARCHAR}


    AND T.GROUP_NAME_EN = #{groupNameEn,jdbcType=VARCHAR}


    AND T.DI = #{groupNameCn,jdbcType=VARCHAR}


    AND T.AGENT_CODE = #{agentCode,jdbcType=VARCHAR}


    AND T.PARTITION_ID = #{partitionId,jdbcType=VARCHAR}

ORDER BY T.PARTITION_ID, T.AGENT_CODE
