mapper.select:
select T.REC_ID, T.CARRIER, T.SUB_COMPANY_ID, T.SUB_COMPANY_DSC, T.PLANE_NO,
T.STAR_DATE, T.END_DATE, T.MONTH, T.REMARK, T.CREATOR_ID, T.CREATOR_NAME,
T.CREATOR_TIME, T.PLANE_USE_MODE, T.COMPANY_CODE
from CTAS_FLT_PLANE_NO T
WHERE T.COMPANY_CODE = #{companyCode,jdbcType=VARCHAR}

    AND T.CARRIER = #{carrier,jdbcType=VARCHAR}


    AND T.PLANE_NO = #{planeNo,jdbcType=VARCHAR}


    AND T.PLANE_USE_MODE = #{planeUseMode,jdbcType=VARCHAR}


    AND T.STAR_DATE >= #{starDate,jdbcType=TIMESTAMP}


    AND T.END_DATE <= #{endDate,jdbcType=TIMESTAMP}

ORDER BY T.CARRIER, T.PLANE_NO
