mapper.select:
select

from CTAS_ROBOT_LOG
where COMPANY_CODE = #{companyCode,jdbcType=VARCHAR}

    AND UPPER(USER_CODE) = #{userCode,jdbcType=VARCHAR}


    AND BILL_TYPE = #{billType,jdbcType=VARCHAR}


    AND BILL_DATE = #{billDate,jdbcType=DECIMAL}


    AND START_TIME  >= #{beginTime,jdbcType=TIMESTAMP}


    AND START_TIME  <= #{endTime,jdbcType=TIMESTAMP}

order by START_TIME desc

mapper.insert:

    select SEQ_CTAS_ROBOT_LOG.nextval as recId from dual

insert into CTAS_ROBOT_LOG (REC_ID, ROBOT_ID, USER_CODE,
START_TIME, FINISHED_TIME, ELAPSE_TIME,
BILL_TYPE, BILL_DATE, COMPANY_CODE
)
values (#{recId,jdbcType=DECIMAL}, #{robotId,jdbcType=VARCHAR}, #{userCode,jdbcType=VARCHAR},
#{startTime,jdbcType=TIMESTAMP}, #{finishedTime,jdbcType=TIMESTAMP}, #{elapseTime,jdbcType=DECIMAL},
#{billType,jdbcType=VARCHAR}, #{billDate,jdbcType=DECIMAL}, #{companyCode,jdbcType=VARCHAR}
)
